mapper.select:
SELECT T1.*,T2.* FROM( SELECT B.BOARD_ID as boardId, B.TITLE, B.READ_CNT as readCnt, B.CONTENTS, B.REG_ID as regId, CASE TO_CHAR(B.REG_DT,'YYYY/MM/DD') WHEN TO_CHAR(SYSDATE,'YYYY/MM/DD') THEN TO_CHAR(SYSDATE,'HH24:MI') ELSE TO_CHAR(B.REG_DT,'YYYY/MM/DD') END regDt, B.rnum as num FROM( SELECT ROWNUM AS rnum,a.* FROM ( SELECT * FROM board WHERE 1=1 --검색조건
ORDER BY reg_dt desc )A WHERE ROWNUM <= (#{pageSize} *(#{pageNum} - 1)+#{pageSize}) )B WHERE rnum >= (#{pageSize}*(#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM board WHERE 1=1 --검색조건
)T2
