mapper.select:
SELECT T1.*,T2.* FROM( SELECT B.u_id, B.name, B.passwd, B.u_level as hLevel, B.login, B.recommend, B.mail as email, CASE TO_CHAR(B.REG_DT,'YYYY/MM/DD') WHEN TO_CHAR(SYSDATE,'YYYY/MM/DD') THEN TO_CHAR(SYSDATE,'HH24:MI') ELSE TO_CHAR(B.REG_DT,'YYYY/MM/DD') END regDt, rnum as num FROM( SELECT ROWNUM rnum, A.* FROM ( SELECT * FROM hr_member WHERE reg_dt > '1900/01/01' --검색조건
ORDER BY reg_dt DESC )A WHERE ROWNUM <= (#{pageSize} *(#{pageNum} - 1)+#{pageSize}) )B WHERE B.RNUM >= (#{pageSize}*(#{pageNum}-1)+1) )T1 CROSS JOIN ( SELECT count(*) totalCnt FROM hr_member WHERE reg_dt > '1900/01/01' --검색조건
)T2
